import React, { useEffect, useState } from 'react';

const Hero = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <section id="hero" className="min-h-screen flex items-center justify-center bg-apple-bg">
      <div className="max-w-6xl mx-auto text-center px-6">
        {/* Main Name - Apple Style */}
        <div className={`fade-in ${isVisible ? 'revealed' : ''}`}>
          <h1 className="heading-xl text-text-primary mb-8">
            DIVLEEN LOTA
          </h1>
        </div>

        {/* Subtitle with delay */}
        <div className={`fade-in ${isVisible ? 'revealed' : ''}`} style={{ transitionDelay: '0.3s' }}>
          <p className="text-2xl sm:text-3xl text-text-secondary mb-16 font-light tracking-wide">
            Computer Science Student â€¢ Software Engineer Intern
          </p>
        </div>

        {/* Minimalist CTA */}
        <div className={`fade-in ${isVisible ? 'revealed' : ''}`} style={{ transitionDelay: '0.6s' }}>
          <div className="flex flex-col sm:flex-row gap-6 justify-center items-center">
            <a
              href="#about"
              className="btn-primary text-lg focus-ring"
            >
              Learn More
            </a>
            <a
              href="mailto:divleen2@gmail.com"
              className="btn-secondary text-lg focus-ring"
            >
              Get In Touch
            </a>
          </div>
        </div>

        {/* Subtle scroll indicator */}
        <div className={`fade-in ${isVisible ? 'revealed' : ''}`} style={{ transitionDelay: '1s' }}>
          <div className="absolute bottom-12 left-1/2 transform -translate-x-1/2">
            <div className="w-6 h-10 border border-text-muted rounded-full flex justify-center">
              <div className="w-1 h-3 bg-text-muted rounded-full mt-2 animate-pulse"></div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
