import React from 'react';

const Footer = () => {
  const currentYear = new Date().getFullYear();

  const socialLinks = [
    {
      name: 'LinkedIn',
      url: 'https://www.linkedin.com/in/divleen-lota/',
      icon: 'üíº'
    },
    {
      name: 'GitHub',
      url: 'https://github.com/divleenlota',
      icon: 'üìÅ'
    },
    {
      name: 'Email',
      url: 'mailto:divleen2@gmail.com',
      icon: 'üìß'
    }
  ];

  const quickLinks = [
    { name: 'About', href: '#about' },
    { name: 'Experience', href: '#experience' },
    { name: 'Projects', href: '#projects' },
    { name: 'Skills', href: '#skills' },
    { name: 'Contact', href: '#contact' }
  ];

  const scrollToSection = (href) => {
    const element = document.querySelector(href);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <footer className="bg-dark-bg border-t border-gray-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
          {/* Brand Section */}
          <div className="lg:col-span-2">
            <div className="mb-6">
              <h3 className="text-2xl font-bold gradient-text mb-4">Divleen Lota</h3>
              <p className="text-text-secondary leading-relaxed max-w-md">
                Computer Science student passionate about AI/ML, full-stack development, 
                and creating innovative solutions that make a real impact.
              </p>
            </div>

            {/* Contact Info */}
            <div className="space-y-2">
              <div className="flex items-center space-x-3 text-text-secondary">
                <span className="w-4 h-4">üìç</span>
                <span>Milpitas, CA</span>
              </div>
              <div className="flex items-center space-x-3 text-text-secondary">
                <span className="w-4 h-4">üéì</span>
                <span>UC Davis - Computer Science</span>
              </div>
              <div className="flex items-center space-x-3 text-text-secondary">
                <span className="w-4 h-4">üíº</span>
                <span>Walmart Global Tech Intern</span>
              </div>
            </div>
          </div>

          {/* Quick Links */}
          <div>
            <h4 className="text-lg font-semibold text-text-primary mb-6">Quick Links</h4>
            <ul className="space-y-3">
              {quickLinks.map((link, index) => (
                <li key={index}>
                  <button
                    onClick={() => scrollToSection(link.href)}
                    className="text-text-secondary hover:text-accent-blue transition-colors duration-200 text-left"
                  >
                    {link.name}
                  </button>
                </li>
              ))}
            </ul>
          </div>

          {/* Social Links */}
          <div>
            <h4 className="text-lg font-semibold text-text-primary mb-6">Connect</h4>
            <div className="space-y-3">
              {socialLinks.map((social, index) => (
                <a
                  key={index}
                  href={social.url}
                  target={social.url.startsWith('http') ? '_blank' : '_self'}
                  rel={social.url.startsWith('http') ? 'noopener noreferrer' : ''}
                  className="flex items-center space-x-3 text-text-secondary hover:text-accent-blue transition-colors duration-200 group"
                >
                  <span className="text-lg group-hover:scale-110 transition-transform">
                    {social.icon}
                  </span>
                  <span>{social.name}</span>
                </a>
              ))}
            </div>

            {/* Resume Download */}
            <div className="mt-6">
              <a
                href="/resume.pdf"
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center space-x-2 bg-gradient-to-r from-accent-blue to-accent-purple text-white px-4 py-2 rounded-lg text-sm font-medium hover:shadow-lg hover:shadow-blue-500/25 transition-all duration-300"
              >
                <span>üìÑ</span>
                <span>Download Resume</span>
              </a>
            </div>
          </div>
        </div>

        {/* Bottom Section */}
        <div className="mt-12 pt-8 border-t border-gray-800">
          <div className="flex flex-col md:flex-row justify-between items-center space-y-4 md:space-y-0">
            <div className="text-text-muted text-sm">
              ¬© {currentYear} Divleen Lota. All rights reserved.
            </div>

            <div className="flex items-center space-x-6 text-text-muted text-sm">
              <span>Built with React & Tailwind CSS</span>
              <span>‚Ä¢</span>
              <span>Designed with ‚ù§Ô∏è</span>
            </div>
          </div>
        </div>

        {/* Back to Top Button */}
        <div className="mt-8 text-center">
          <button
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
            className="inline-flex items-center space-x-2 text-text-muted hover:text-accent-blue transition-colors duration-200 group"
          >
            <span className="group-hover:-translate-y-1 transition-transform">‚Üë</span>
            <span>Back to top</span>
          </button>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
